<template>    <div class="chat-room-window">        <div class="message">            <div v-for="item in messageList">                <MyMessage v-if="item.fromId === myId && item.type === 5" :time="timeFormat(item.time)"                           :avatar="myAvatar"                           :message="item.message" :key="item.id"/>                <OtherMessage v-else-if="item.fromId !== myId && item.type === 5" :time="timeFormat(item.time)"                              :avatar="item.avatar"                              :message="item.message" :key="item.id"/>            </div>        </div>    </div></template><script>import MyMessage from '@/components/MyMessage'import OtherMessage from '@/components/OtherMessage'import eventBus from '@/utils/eventBus'export default {    name: 'ChatRoomWindow',    components: {MyMessage, OtherMessage},    data(){        return {            messageList: [],            myId: '',            myAvatar: ''        }    },    methods: {        timeFormat(time) {            if (time instanceof Date) {                return time.toLocaleString().replace("/", "-").replace("/", "-")            }            return time.replace("T", " ")        }    },    created() {        let user = JSON.parse(localStorage.getItem('user'))        this.myId = user.id        this.myAvatar = user.avatar        eventBus.$on('addChatMessage', val => {            this.messageList = [...this.messageList, val]        })    }}</script><style scoped>.chat-room-window {    width: 100%;    height: 450px;    overflow-y: auto;}.message {    padding: 0 20px;}.chat-room-window::-webkit-scrollbar { /*滚动条整体样式*/    width: 4px; /*高宽分别对应横竖滚动条的尺寸*/    height: 4px;    scrollbar-arrow-color: red;}.chat-room-window::-webkit-scrollbar-thumb { /*滚动条里面小方块*/    border-radius: 5px;    -webkit-box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.2);    background: rgba(0, 0, 0, 0.2);    scrollbar-arrow-color: red;}.chat-room-window::-webkit-scrollbar-track { /*滚动条里面轨道*/    -webkit-box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.2);    border-radius: 0;    background: rgba(0, 0, 0, 0);}</style>