<template>    <div ref="item" class="user-info" @click="to(toPath)">        <img class="avatar" :src="avatar" alt="">        <div class="info">            <div class="username">{{username}}</div>            <div class="synopsis">{{synopsis}}</div>        </div>        <i class="el-icon-close" @click="deleteHistory"></i>    </div></template><script>import request from '@/utils/request'export default {    name: 'Item',    props: ['id', 'avatar', 'username', 'synopsis', 'toPath'],    methods: {        to(path){            this.$router.push({                path: path,                query: {                    username: this.username,                    avatar: this.avatar                }            })        },        deleteHistory(){            request.post('/history/delete/' + this.id).then((data)=>{                if (data.data.code === 0) {                    this.$message.success(data.data.data)                    this.$refs.item.remove()                    this.$destroy()                } else {                    this.$message.error(data.data.msg)                }            })        }    }}</script><style scoped>i{    opacity: 0;    cursor: pointer;}.user-info{    display: flex;    height: 44px;    padding: 10px;}.user-info:hover{    background-color: #d6d5d5;}.user-info:hover i {    opacity: 1;}.avatar{    flex: 1;    width: 40px;    height: 40px;    margin-right: 10px;}.info{    flex: 4;    display: flex;    flex-direction: column;}.username{    font-size: 14px;}.synopsis{    font-size: 13px;    color: #999999;    padding-top: 3px;}</style>