<template>    <div class="my-file">        <div>            <span class="time">{{ time }}</span>            <div class="file" @click="download">                <span>{{ format(message) }}</span>            </div>        </div>        <img class="avatar" :src="my_avatar" alt="">    </div></template><script>export default {    name: 'MyFile',    props: ['message', 'avatar', 'time'],    data(){        return {            my_avatar: ''        }    },    methods: {        download(){            let a = document.createElement('a')            a.href = this.message            a.click()        },        format(filename){            let split = filename.split("/")            let splitElement = split[split.length - 1]            let split1 = splitElement.split(".")            return split1[0].substring(0, 10) + '...' + split1[1]        }    },    created() {        let user = JSON.parse(localStorage.getItem("user"))        this.my_avatar = user.avatar    }}</script><style scoped>.my-file{    display: flex;    align-items: flex-start;    justify-content: flex-end;    padding: 10px;    height: 100px;}.my-file .avatar{    width: 40px;    height: 40px;    padding: 6px;}.my-file .file{    width: 80px;    height: 80px;    background-color: #dddddd;    padding: 10px;    border-radius: 5px;}.my-file .time {    font-size: 12px;    color: #999999;}</style>