<template>    <div class="my-message">        <div class="left">            <span class="time">{{ time }}</span>            <div class="bubble"><span>{{ message }}</span></div>        </div>        <div class="right">            <img class="avatar" :src="my_avatar" alt="">        </div>    </div></template><script>export default {    name: 'MyMessage',    props: ['avatar', 'time', 'message'],    data(){        return {            my_avatar: ''        }    },    created() {        let user = JSON.parse(localStorage.getItem("user"))        this.my_avatar = user.avatar    }}</script><style scoped>.my-message{    height: 40px;    display: flex;    justify-content: flex-end;    padding: 10px;}.my-message .left{    height: 40px;    display: flex;    flex-direction: column;    align-items: flex-end;}.my-message .left .bubble{    flex: 3;    background-color: #95ec69;    line-height: 30px;    border-bottom-left-radius: 8px;    border-top-left-radius: 8px;    border-bottom-right-radius: 8px;}.my-message .left .bubble span{    padding: 10px;    font-size: 14px;}.my-message .right .avatar{    width: 40px;    height: 40px;    padding: 6px;}.my-message .left .time{    font-size: 12px;    color: #999999;    flex: 1;}</style>