<template>    <div class="my-pic">        <div>            <span class="time">{{ time }}</span>            <div class="file" @click="download">                <img :src="message" alt="">            </div>        </div>        <img class="avatar" :src="my_avatar" alt="">    </div></template><script>export default {    name: 'MyPic',    props: ['filename', 'message', 'avatar', 'time'],    data() {        return {            my_avatar: ''        }    },    methods: {        download() {            let a = document.createElement('a')            a.href = this.message            a.click()        }    },    created() {        let user = JSON.parse(localStorage.getItem("user"))        this.my_avatar = user.avatar    }}</script><style scoped>.my-pic {    display: flex;    align-items: flex-start;    justify-content: flex-end;    padding: 10px;    height: 100px;}.my-pic .avatar {    width: 40px;    height: 40px;    padding: 6px;}.my-pic .file {    width: 80px;    height: 80px;    background-color: #dddddd;    padding: 10px;    border-radius: 5px;}.my-pic .file img{    width: 80px;    height: 80px;}.my-pic .file span {    line-height: 80px;}.my-pic .file span {    line-height: 80px;}.my-pic .time {    font-size: 12px;    color: #999999;}</style>