<template>    <div ref="item" class="item">        <div class="user">            <img :src="avatar" alt="" class="avatar">            <div class="user-info">                <span class="username">{{username}}</span>                <span class="reason">{{reason}}</span>            </div>            <i class="el-icon-check" @click="agree"></i>            <i class="el-icon-close" @click="refuse"></i>        </div>    </div></template><script>import request from '@/utils/request'import eventBus from '@/utils/eventBus'export default {    name: 'NewFriendItem',    props: ['id', 'username', 'avatar', 'reason', 'synopsis'],    methods: {        agree(){            request.post("/addressBook/agree/" + this.id).then((data)=>{                if (data.data.code === 0) {                    this.destroyElement()                } else {                    this.$message.error(data.data.msg)                }            })            eventBus.$emit('addAddressBook', {id: this.id, username: this.username, avatar: this.avatar, synopsis: this.synopsis})        },        refuse() {            request.post("/addressBook/refuse/" + this.id).then((data) => {                if (data.data.code === 0) {                    this.destroyElement()                } else {                    this.$message.error(data.data.msg)                }            })        },        destroyElement(){            this.$refs.item.remove()            this.$destroy()        }    }}</script><style scoped>.item{    width: 400px;    height: 70px;    border-bottom: 1px solid #d4d4d5;}.user{    padding: 10px 0;    display: flex;    justify-content: space-between;    align-items: center;}.avatar{    width: 50px;    height: 50px;    flex: 1;}.user-info{    display: flex;    flex-direction: column;    flex: 5;}.user-info span{    font-size: 12px;    padding: 4px;}.user-info .reason{    color: #999999;}.user-info button{    flex: 1;}.user i{    margin: 0 5px;    cursor: pointer;}.user .el-icon-check{    color: #74dd92;}.user .el-icon-close {    color: #e34545;}</style>