<template>    <div class="other-file">        <img class="avatar" :src="avatar" alt="">        <div>            <span class="time">{{ time }}</span>            <div class="file" @click="download">                <span>{{ format(message) }}</span>            </div>        </div>    </div></template><script>export default {    name: 'OtherFile',    props: ['message', 'avatar', 'time'],    methods: {        download() {            let a = document.createElement('a')            a.href = this.message            a.click()        },        format(filename) {            let split = filename.split("/")            let splitElement = split[split.length - 1]            let split1 = splitElement.split(".")            return split1[0].substring(0, 10) + '...' + split1[1]        }    }}</script><style scoped>.other-file {    display: flex;    align-items: flex-start;    padding: 10px;    height: 100px;}.other-file .avatar {    width: 40px;    height: 40px;    padding: 6px;}.other-file .file {    width: 80px;    height: 80px;    background-color: #dddddd;    padding: 10px;    border-radius: 5px;}.other-file .time {    font-size: 12px;    color: #999999;}</style>