<template>    <div class="other-pic">        <img class="avatar" :src="avatar" alt="">        <div>            <span class="time">{{ time }}</span>            <div class="file" @click="download">                <img :src="message" alt="">            </div>        </div>    </div></template><script>export default {    name: 'OtherPic',    props: ['filename', 'message', 'avatar', 'time'],    methods: {        download() {            let a = document.createElement('a')            a.href = this.message            a.click()        }    }}</script><style scoped>.other-pic {    display: flex;    align-items: flex-start;    padding: 10px;    height: 100px;}.other-pic .avatar {    width: 40px;    height: 40px;    padding: 6px;}.other-pic .file {    width: 80px;    height: 80px;    background-color: #dddddd;    padding: 10px;    border-radius: 5px;}.other-pic .file img {    width: 80px;    height: 80px;}.other-pic .file span {    line-height: 80px;}.other-pic .time {    font-size: 12px;    color: #999999;}</style>