<template>    <div class="user-info" @click="to(toPath)">        <img class="avatar" :src="avatar" alt="">        <div class="info">            <div class="username">{{ username }}</div>        </div>    </div></template><script>export default {    name: 'news',    props: ['avatar', 'username', 'toPath', 'synopsis'],    methods: {        to(path) {            this.$router.push({                path: path,                query: {                    username: this.username,                    synopsis: this.synopsis,                    avatar: this.avatar                }            })        }    }}</script><style scoped>.user-info {    display: flex;    height: 44px;    padding: 10px;}.user-info:hover {    background-color: #d6d5d5;}.avatar {    flex: 1;    width: 40px;    height: 40px;    margin-right: 10px;}.info {    flex: 4;    display: flex;    align-items: center;}.username {    margin-top: -10px;    font-size: 14px;}</style>