<template>    <div class="new-friends">        <div class="title">            <p>新的朋友</p>        </div>        <div class="new-friends-list">            <NewFriendItem v-for="item in newFriendsList" :id="item.id" :avatar="item.avatar" :username="item.username" :synopsis="item.synopsis" :reason="item.reason" :key="item.id" />        </div>    </div></template><script>import NewFriendItem from '@/components/NewFriendItem'import request from '@/utils/request'export default {    name: 'NewFriends',    components: {NewFriendItem},    data(){        return {            newFriendsList: []        }    },    created() {        request.post("/addressBook/newFriends").then((data)=>{            if (data.data.code === 0) {                this.newFriendsList = data.data.data            } else {                this.$message.error(data.data.msg)            }        })    }}</script><style scoped>.new-friends{    height: 650px;    width: 600px;    display: flex;    flex-direction: column;    border-bottom-right-radius: 2px;    border-top-right-radius: 2px;    background-color: #f5f5f5;}.title{    height: 57px;    border-bottom: 1px solid #d4d4d5;}.title p{    font-size: 18px;    padding-top: 4px;    padding-left: 30px;}.new-friends-list{    margin: 0 auto;    overflow-y: auto;}.new-friends-list::-webkit-scrollbar { /*滚动条整体样式*/    width: 4px; /*高宽分别对应横竖滚动条的尺寸*/    height: 4px;    scrollbar-arrow-color: red;}.new-friends-list::-webkit-scrollbar-thumb { /*滚动条里面小方块*/    border-radius: 5px;    -webkit-box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.2);    background: rgba(0, 0, 0, 0.2);    scrollbar-arrow-color: red;}.new-friends-list::-webkit-scrollbar-track { /*滚动条里面轨道*/    -webkit-box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.2);    border-radius: 0;    background: rgba(0, 0, 0, 0);}</style>