<template>    <div class="user-detail">        <div class="detail">            <img class="avatar" :src="avatar" alt="">            <div class="info">                <span class="username">{{username}}</span>                <span class="synopsis">简介：<span>{{ synopsis }}</span></span>            </div>        </div>        <div class="option">            <el-button type="primary" size="small" @click="toChat">发消息</el-button>            <el-button type="danger" size="small" @click="remove">删除</el-button>        </div>    </div></template><script>import request from '@/utils/request'import eventBus from '@/utils/eventBus'export default {    name: 'UserDetail',    data(){        return {            id: this.$route.params.id,            username: this.$route.query.username,            synopsis: this.$route.query.synopsis,            avatar: this.$route.query.avatar,        }    },    methods: {        remove(){            this.$confirm('是否删除？', '删除').then(()=>{                request.post("/addressBook/remove/" + this.id).then((data) => {                    if (data.data.code === 0) {                        this.$message.success(data.data.data)                    } else {                        this.$message.error(data.data.msg)                    }                })            }).catch(()=>{                this.$message.info('取消删除')            })        },        toChat(){            this.$router.push({                path: '/history/' + this.id,                query: {                    username: this.username,                    avatar: this.avatar                }            })        }    }}</script><style scoped>.user-detail{    height: 650px;    width: 600px;    display: flex;    flex-direction: column;    border-bottom-right-radius: 2px;    border-top-right-radius: 2px;    background-color: #f5f5f5;    align-items: center;    justify-content: center;}.user-detail .detail{    display: flex;    padding: 10px;    width: 300px;    border-bottom: 1px solid #d4d4d5;}.detail .info{    display: flex;    flex-direction: column;    justify-content: center;}.detail .info .synopsis{    font-size: 14px;    color: #9e9e9e;}.detail .info>span{    padding: 4px 10px;}.detail .avatar{    height: 60px;    width: 60px;}.option button{    margin: 20px;}</style>